import { MagnifyingGlassIcon } from "@heroicons/react/24/solid";
import React from 'react';
import Select from 'react-select';

interface MultiSelectCityProps {
    className?: string; 
    roundedLeft?: boolean; 
    roundedRight?: boolean;
}

interface SearchInputProps {
    placeholder?: string;
    value?: string;
    onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
    className?: string;
    roundedLeft?: boolean;
    roundedRight?: boolean;
}

export const SearchInput: React.FC<SearchInputProps> = ({placeholder = "Suchen...",value,onChange,className,roundedLeft = false,roundedRight = false
}) => {
    const getRoundedClasses = () => {
        if (roundedLeft && roundedRight) return 'rounded-full';
        if (roundedLeft) return 'rounded-l-full';
        if (roundedRight) return 'rounded-r-full';
        return 'rounded-none';
    };

    return (
        // Beachte: Der `className` Prop wird an diesen äußeren div-Container weitergegeben.
        // Die interne Border wird hier entfernt und vom Formular-Container übernommen.
        <div className={`relative flex items-center ${className || ''}`}>
            <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
            <input
                type="text"
                placeholder={placeholder}
                value={value}
                onChange={onChange}
                className={`
                    block w-full py-2 pl-10 pr-3 // Padding für Höhe und Textposition
                    focus:outline-none focus:ring-2 focus:ring-blue-500
                    text-gray-900 placeholder:text-gray-400
                    ${getRoundedClasses()}
                `}
            />
        </div>
    );
};

export function MultiSelectCity({ className, roundedLeft = false, roundedRight = false }: MultiSelectCityProps) {
    const options = [
        { value: '1', label: 'München' },
        { value: '2', label: 'Stuttgart' },
        { value: '3', label: 'Mannheim' }
    ];

    const getRoundedClasses = () => {
        if (roundedLeft && roundedRight) return 'rounded-full';
        if (roundedLeft) return 'rounded-l-full';
        if (roundedRight) return 'rounded-r-full';
        return 'rounded-none';
    };

    return (
        <div className={`relative ${className || ''}`}>
            <Select
                isMulti
                name="Ort auswählen"
                options={options}
                className={`basic-multi-select w-full ${getRoundedClasses()}`} // Tailwind-Rundung hier anwenden
                classNamePrefix="select"
                placeholder="Ort auswählen"
                styles={{
                    control: (baseStyles, state) => ({
                        ...baseStyles,
                        // === WICHTIGE ANPASSUNGEN HIER ===
                        borderRadius: '0px', // EXTREM WICHTIG: Überschreibt Standard-Rundung
                        border: 'none',     // EXTREM WICHTIG: Überschreibt Standard-Border
                        boxShadow: 'none',  // EXTREM WICHTIG: Entfernt Standard-Shadow bei Fokus
                        '&:hover': {
                            borderColor: 'none', // Auch hier die Hover-Border entfernen
                        },
                        minHeight: 'auto', // Damit es sich strecken lässt
                        height: 'auto',    // Wichtig, um py-2 vom Parent zu nutzen
                        paddingTop: '8px', // Simuliert py-2 des Inputs (2px border + 6px padding)
                        paddingBottom: '8px', // Simuliert py-2 des Inputs
                        // ===============================
                    }),
                    valueContainer: (baseStyles) => ({
                        ...baseStyles,
                        padding: '0 8px', // Anpassung des internen Paddings
                    }),
                    input: (baseStyles) => ({
                        ...baseStyles,
                        margin: '0', // Entfernt standardmäßigen Margin
                        padding: '0', // Entfernt standardmäßigen Padding
                    }),
                    placeholder: (baseStyles) => ({
                        ...baseStyles,
                        margin: '0', // Entfernt standardmäßigen Margin
                        padding: '0', // Entfernt standardmäßigen Padding
                    }),
                    indicatorsContainer: (baseStyles) => ({
                        ...baseStyles,
                        padding: '0 8px', // Anpassung des Paddings für Icons (Dropdown-Pfeil)
                    }),
                    // Du musst eventuell auch 'menu' und 'option' Styles anpassen,
                    // falls deren Rundungen nicht passen, aber das ist für die sichtbare Leiste nicht entscheidend.
                }}
            />
        </div>
    );
}
