import React from "react";
import {MultiSelectCity} from "./elements";
import {SearchInput} from "./elements";


export function SearchBar() {
    const [searchValue, setSearchValue] = React.useState("");

    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchValue(event.target.value);
    }

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        alert("Search submitted!");
    }
    return (
        <form className="flex sm:flex-row flex-col gap-y-3 sm: items-center sm:justify-center p-4 bg-gray-100 sm:space-x-4" onSubmit={handleSubmit}>
            <input type="text" placeholder={"Was willst du suchen?"} className="w-full py-2 sm:max-w-48  border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500
            bg-no-repeat " value={searchValue} onChange={handleSearchChange} />
            <SearchInput
                placeholder="Was suchst du?"
                value={searchValue}
                onChange={handleSearchChange}
                className="w-full sm:flex-1 sm:max-w-xs" // Breite für das Suchfeld
            />
            <MultiSelectCity />
            <button type="submit" className="px-4 py-2 min-w-35 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500">
                Suchen
            </button>
        </form>
    );
}
