import React, { useState } from "react";
import { MultiSelectCity } from "./elements"; 
import { SearchInput } from "./elements";


export function SearchBar() {
    const [searchValue, setSearchValue] = useState('');

    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchValue(event.target.value);
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        alert(`Suche gestartet mit: ${searchValue}`);
    }

    return (
        // Formular als Container für die gesamte Suchleiste
        <form
            className="
                flex flex-col items-center gap-y-3 // Mobile: untereinander, vertikaler Abstand
                sm:flex-row sm:justify-center sm:space-x-0 sm:items-stretch // Desktop: nebeneinander, KEIN Abstand, gleiche Höhe
                sm:border sm:border-gray-300 sm:rounded-full sm:p-0 // Desktop: Gemeinsame Border, abgerundet, kein internes Padding
                bg-gray-100 // Hintergrundfarbe für Mobile
                sm:bg-white // Weißer Hintergrund auf Desktop für die Input-Felder
            "
            onSubmit={handleSubmit}
        >
            {/* SearchInput: links abgerundet, rechts keine Rundung, keine eigene Border rechts */}
            <SearchInput
                placeholder="Was suchst du?"
                value={searchValue}
                onChange={handleSearchChange}
                className="w-full sm:flex-1 sm:max-w-xs sm:border-r-0" // <-- Wichtig: rechte Border entfernen
                roundedLeft={true}
                roundedRight={false}
            />

            {/* MultiSelectCity: keine Rundung, keine eigene Border links/rechts */}
            <MultiSelectCity
                className="w-full sm:flex-1 sm:max-w-xs sm:rounded-none sm:border-x-0" // <-- Wichtig: keine Rundung, links/rechts Border entfernen
                roundedLeft={false}
                roundedRight={false}
            />

            {/* Button: rechts abgerundet, links keine Rundung, keine eigene Border links */}
            <button
                type="submit"
                className="
                    w-full px-4 py-2 bg-blue-500 text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500
                    sm:w-auto sm:rounded-l-none sm:rounded-r-full sm:border-l-0 // <-- Wichtig: links Border entfernen
                    sm:py-2 // Sicherstellen, dass Padding zur Höhe passt
                "
            >
                Suchen
            </button>
        </form>
    );
}
