import { useState } from "react";
import { NavigationLink } from "./compnents"; // eigene Link-Komponente

const Header = () => {
  const [selectedUser, setSelectedUser] = useState("Benutzer A");
  const users = ["Benutzer A", "Benutzer B", "Benutzer C"];

  return (
    <header className="bg-white shadow p-4 flex flex-col md:flex-row items-center justify-between gap-4">
      {/* Benutzer-Auswahl */}
      <div className="flex items-center gap-2">
        <label htmlFor="user-select" className="text-sm font-medium text-gray-700">Benutzer:</label>
        <select
          id="user-select"
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
          className="border border-gray-300 rounded px-2 py-1 text-sm"
        >
          {users.map((user) => (
            <option key={user} value={user}>
              {user}
            </option>
          ))}
        </select>
      </div>

      {/* Navigation Buttons */}
      <nav className="flex gap-4">
        <NavigationLink href="/">Start</NavigationLink>
        <NavigationLink href="/about">Ãœber uns</NavigationLink>
        <NavigationLink href="/kontakt">Kontakt</NavigationLink>
      </nav>
    </header>
  );
};

export default Header;
