import Select from 'react-select';

interface SearchInputProps {
    placeholder?: string;
    value?: string;
    onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
    // Fügen Sie hier weitere Props hinzu, die Sie an das input-Feld weiterleiten möchten
    // z.B. name, id, className etc.
    className?: string; // Für zusätzliche Styling-Klassen von außen
}

export function MultiSelectCity() {
    const options = [
        { value: '1', label: 'München' },
        { value: '2', label: 'Stuttgart' },
        { value: '3', label: 'Mannheim' }
    ];
    return (
        <Select isMulti name="Ort auswählen"
            options={options}
            className="basic-multi-select w-full sm:flex-1 sm:max-w-48"
            classNamePrefix="select"
            placeholder="Ort auswählen"
        />
    );
}


export const SearchInput: React.FC<SearchInputProps> = ({
    placeholder = "Suchen...", // Standard-Placeholder, wenn keiner übergeben wird
    value,
    onChange,
    className
}) => {
    return (
        <div className={`relative flex items-center ${className || ''}`}>
            {/* Icon-Container */}
            <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
            {/* Das tatsächliche Input-Feld */}
            <input
                type="text"
                placeholder={placeholder}
                value={value}
                onChange={onChange}
                className="
          block w-full py-2 pl-10 pr-3 border border-gray-300 rounded-full // 'rounded-full' für starke Rundung
          focus:outline-none focus:ring-2 focus:ring-blue-500
          text-gray-900 placeholder:text-gray-400
        "
            // Hier können Sie weitere Standard-Props hinzufügen, die immer gelten sollen
            // Oder diese als Props der Komponente übergeben, wenn sie konfigurierbar sein sollen
            />
        </div>
    );
};
