import { MagnifyingGlassIcon } from "@heroicons/react/24/solid";

interface SearchInputProps {
    placeholder?: string;
    value?: string;
    onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
    className?: string;
    // Hinzugefügte Props für Rundung
    roundedLeft?: boolean;
    roundedRight?: boolean;
}

export const SearchInput: React.FC<SearchInputProps> = ({
    placeholder = "Suchen...",
    value,
    onChange,
    className,
    roundedLeft = false, // Standardmäßig keine Rundung, wird von Parent gesteuert
    roundedRight = false // Standardmäßig keine Rundung, wird von Parent gesteuert
}) => {
    // Dynamische Rundungs-Klassen basierend auf Props
    const getRoundedClasses = () => {
        if (roundedLeft && roundedRight) return 'rounded-full'; // Für den Fall, dass beides gewünscht ist
        if (roundedLeft) return 'rounded-l-full';
        if (roundedRight) return 'rounded-r-full';
        return 'rounded-none'; // Keine Rundung, wenn beides false ist
    };

    return (
        <div className={`relative flex items-center ${className || ''}`}>
            {/* Icon-Container */}
            <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
            {/* Das tatsächliche Input-Feld */}
            <input
                type="text"
                placeholder={placeholder}
                value={value}
                onChange={onChange}
                className={`
                    block w-full py-2 pl-10 pr-3 border border-gray-300
                    focus:outline-none focus:ring-2 focus:ring-blue-500
                    text-gray-900 placeholder:text-gray-400
                    ${getRoundedClasses()} // Dynamische Rundung
                `}
            />
        </div>
    );
};
