// SearchBar.tsx
import React, { useState } from "react";
import { MultiSelectCity } from "./elements"; // Stelle sicher, dass dies der korrekte Pfad ist
import { SearchInput } from './SearchInput'; // Stelle sicher, dass dies der korrekte Pfad ist

export function SearchBar() {
    const [searchValue, setSearchValue] = useState('');

    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchValue(event.target.value);
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        alert(`Suche gestartet mit: ${searchValue}`);
    }

    return (
        <form
            className="
                flex flex-col items-center p-4 bg-gray-100 gap-y-3 // Mobile: untereinander, vertikaler Abstand
                sm:flex-row sm:justify-center sm:space-x-0 sm:items-stretch // Desktop: nebeneinander, KEIN Abstand, gleiche Höhe
            "
            onSubmit={handleSubmit}
        >
            {/* SearchInput: links abgerundet, rechts keine Rundung */}
            <SearchInput
                placeholder="Was suchst du?"
                value={searchValue}
                onChange={handleSearchChange}
                className="w-full sm:flex-1 sm:max-w-xs" // Breite für das Suchfeld
                roundedLeft={true}
                roundedRight={false}
            />

            {/* MultiSelectCity: keine Rundung an beiden Seiten auf Desktop */}
            <MultiSelectCity
                className="w-full sm:flex-1 sm:max-w-xs sm:rounded-none // Desktop: keine Rundung, aber Mobile hat 4px default
                " // <- Die sm:rounded-none-Klasse wird hier direkt angewendet
                roundedLeft={false} // Explizit für die Komponente setzen
                roundedRight={false} // Explizit für die Komponente setzen
            />

            {/* Button: rechts abgerundet, links keine Rundung */}
            <button
                type="submit"
                className="
                    w-full px-4 py-2 bg-blue-500 text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500
                    sm:w-auto sm:rounded-l-none sm:rounded-r-full // Desktop: links keine Rundung, rechts volle Rundung
                "
            >
                Suchen
            </button>
        </form>
    );
}
