import { useState } from "react";
import { Menu, X } from "lucide-react";
import { NavigationLink } from "./compnents";

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex-shrink-0 text-xl font-bold text-blue-600">
            alle Beiträge
          </div>

          {/* Desktop Nav */}
          <nav className="hidden md:flex space-x-6">
            <NavigationLink href="/about">Über uns</NavigationLink>
            <NavigationLink href="/">Start</NavigationLink>
           
          </nav>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button onClick={() => setMenuOpen(!menuOpen)} className="text-gray-600 hover:text-blue-600">
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Nav */}
      {menuOpen && (
        <div className="md:hidden px-4 pb-4 space-y-2">
          <a href="#" className="block text-gray-600 hover:text-blue-600">Start</a>
          <a href="#" className="block text-gray-600 hover:text-blue-600">Über uns</a>
          <a href="#" className="block text-gray-600 hover:text-blue-600">Kontakt</a>
        </div>
      )}
    </header>
  );
};

export default Header;
